<script>
import Menu from '@/components/header/menu/Menu.vue'
import dayjs from 'dayjs'
import shortid from 'shortid'
export default {
  name: 'Navbar',
  components: {
    Menu
  },
  props: ['isOnHomepage'],
  data () {
    return {
      isHomepage: this.isOnHomepage,
      isOnTheTop: true,
      displayMobileBar: false,
      topPosition: 0,
      isOpen: false,
      dialogVisible: false,
      date: {
        start: '',
        end: ''
      },
      guests: {
        numOfAdultGuests: 1,
        numOfChildrenGuest: 0
      }
    }
  },
  mounted () {
    this.$nextTick(function () {
      window.addEventListener('scroll', this.onScroll)
    })
  },
  updated () {
    this.$nextTick(function () {
      window.addEventListener('scroll', this.onScroll)
    })
  },
  destroyed () {
    window.removeEventListener('scroll', this.onScroll)
  },
  methods: {
    // Computed scroll event: to calculate how far between current position of the
    // window and the absolute top of window
    onScroll () {
      const that = this
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop
      that.isOnTheTop = scrollTop === 0
      that.displayMobileBar = scrollTop > 0
    },
    // Submit search request for making reservation through dialog box
    async setSearchChoice () {
      const createTime = dayjs().toISOString()
      if (dayjs(this.date.start).diff(dayjs(this.date.end)) === 0 ||
        this.date.start === null ||
        this.date.end === null) {
        alert('Please select a different date.')
      } else {
        const id = shortid.generate()
        const selection = {
          date: this.date,
          guests: this.guests,
          time: createTime
        }
        await this.$store.dispatch('generateId', id)
        await this.$store.dispatch('searchRoomType', selection)
        await this.$router.push({
          name: 'Reservation',
          params: { tempId: id },
          query: {
            createdTime: dayjs(createTime).format('YYYY-MM-DD'),
            currentStep: 's1'
          }
        })
      }
    }
  },
  computed: {
    // To add class to navbar while toggle scroll event
    scrollClass: function () {
      return {
        'navbar--scroll': this.displayMobileBar
      }
    },
    // To change the navbar color while at homepage and on the top of the page
    homepageColorClass: function () {
      return {
        'navbar--homepage': this.isOnTheTop && this.isHomepage,
        'nav-mobile': this.displayMobileBar && this.isOpen
      }
    },
    // To control the menu button from open icon to close icon
    menuButtonClass: function () {
      return {
        'button-open': this.isOpen
      }
    },
    // To display overlay menu from hidden
    menuDisplayClass: function () {
      return {
        'menu-display': this.isOpen
      }
    },
    // To prevent background color change when overlay menu open and scroll down
    // at the same time
    menuScrollClass: function () {
      return {
        'menu-scroll': this.isOpen && !this.isOnTheTop
      }
    }
  }
}
</script>
